'''Caesar encryption and de-encryption'''
def caesar_encryp(text , s):
    result = ""

    for char in text:
        if char.isupper():
            print('some')
            result +=chr((ord(char) + s - 65) % 26 + 65)
        else:
            result +=chr((ord(char) + s - 97) % 26 + 97)
        
    return result

def caesar_deencrypt(encryp_text , s):
    result = ""
    for char in encryp_text:
        if char.isupper():
            result += chr((ord(char) - s - 65) % 26 + 65)
        else:
            result +=chr((ord(char) -s-97) %26 +97)

    return result


encypt = caesar_encryp('rachit' , 3)
deencryp = caesar_deencrypt(encypt , 3)

print(encypt)
print(deencryp)



